00101     private void HandleExpansionChevronOnMouseDown(TreeViewNoType localTreeViewNoType)
00102     {
00103         if (!localTreeViewNoType.IsExpandable)
00104             return;
00105 
00106         localTreeViewNoType.IsExpanded = !localTreeViewNoType.IsExpanded;
00107 
00108         if (localTreeViewNoType.IsExpanded)
00109         {
00110             BackgroundTaskService.Enqueue(
00111                 Key<IBackgroundTask>.NewKey(),
00112                 ContinuousBackgroundTaskWorker.GetQueueKey(),
00113                 "TreeView.HandleExpansionChevronOnMouseDown",
00114                 async () => 
00115                 {
00116                     await localTreeViewNoType.LoadChildListAsync().ConfigureAwait(false);
00117                     TreeViewService.ReRenderNode(TreeViewContainer.Key, localTreeViewNoType);
00118                 });
00119         }
00120         else
00121         {
00122             TreeViewService.ReRenderNode(TreeViewContainer.Key, localTreeViewNoType);
00123         }
00124     }
