00126     private async Task ManuallyPropagateOnContextMenu(
00127         MouseEventArgs mouseEventArgs,
00128         TreeViewContainer treeViewContainer,
00129         TreeViewNoType treeViewNoType)
00130     {
00131         var treeViewCommandArgs = new TreeViewCommandArgs(
00132             TreeViewService,
00133             TreeViewContainer,
00134             TreeViewNoType,
00135             FocusAsync,
00136             null,
00137             mouseEventArgs,
00138             null);
00139 
00140         await TreeViewMouseEventHandler
00141             .OnMouseDownAsync(treeViewCommandArgs)
00142             .ConfigureAwait(false);
00143 
00144         BackgroundTaskService.Enqueue(
00145             Key<IBackgroundTask>.NewKey(),
00146             ContinuousBackgroundTaskWorker.GetQueueKey(),
00147             "TreeView.ManuallyPropagateOnContextMenu",
00148             async () => await HandleTreeViewOnContextMenu.Invoke(
00149                     mouseEventArgs,
00150                     treeViewContainer.Key,
00151                     treeViewNoType)
00152                 .ConfigureAwait(false));
00153     }
