00203     private Task HandleOnKeyDown(KeyboardEventArgs keyboardEventArgs)
00204     {
00205         switch (keyboardEventArgs.Key)
00206         {
00207             case "ContextMenu":
00208             {
00209                 var mouseEventArgs = new MouseEventArgs { Button = -1 };
00210                 return ManuallyPropagateOnContextMenu(mouseEventArgs, TreeViewContainer, TreeViewNoType);
00211             }
00212             case ".":
00213             {
00214                 if (keyboardEventArgs.CtrlKey)
00215                 {
00216                     var mouseEventArgs = new MouseEventArgs { Button = -1 };
00217                     return ManuallyPropagateOnContextMenu(mouseEventArgs, TreeViewContainer, TreeViewNoType);
00218                 }
00219                 break;
00220             }
00221         }
00222     
00223         return Task.CompletedTask;
00224     }
