00241         private static void PerformMarkForRerender(TreeViewNoType node)
00242         {
00243             var markForRerenderTarget = node;
00244 
00245             while (markForRerenderTarget is not null)
00246             {
00247                 markForRerenderTarget.TreeViewChangedKey = Key<TreeViewChanged>.NewKey();
00248                 markForRerenderTarget = markForRerenderTarget.Parent;
00249             }
00250         }
