00253             TreeViewContainer inContainer,
00254             ReRenderNodeAction reRenderNodeAction)
00255         {
00256             PerformMarkForRerender(reRenderNodeAction.Node);
00257             return inContainer with { StateId = Guid.NewGuid() };
00258         }
