00260         private static TreeViewContainer PerformSetActiveNode(
00261             TreeViewContainer inContainer,
00262             SetActiveNodeAction setActiveNodeAction)
00263         {
00264             if (inContainer.ActiveNode is not null)
00265                 PerformMarkForRerender(inContainer.ActiveNode);
00266 
00267             if (setActiveNodeAction.NextActiveNode is not null)
00268                 PerformMarkForRerender(setActiveNodeAction.NextActiveNode);
00269 
00270             var inSelectedNodeList = inContainer.SelectedNodeList;
00271             var selectedNodeListWasCleared = false;
00272 
00273             TreeViewContainer outContainer;
00274 
00275             // TODO: I'm adding multi-select. I'd like to single out the...
00276             // ...SelectNodesBetweenCurrentAndNextActiveNode case for now...
00277             // ...and DRY the code after. (2024-01-13) 
00278             if (setActiveNodeAction.SelectNodesBetweenCurrentAndNextActiveNode)
00279             {
00280                 outContainer = inContainer;
00281                 int direction;
00282 
00283                 // Step 1: Determine the selection's direction.
00284                 //
00285                 // That is to say, on the UI, which node appears
00286                 // vertically closer to the root node.
00287                 //
00288                 // Process: -Discover the closest common ancestor node.
00289                 //          -Then backtrack one node depth.
00290                 //          -One now has the two nodes at which
00291                 //              they differ.
00292                 //        -Compare the 'TreeViewNoType.IndexAmongSiblings'
00293                 //          -Next.IndexAmongSiblings - Current.IndexAmongSiblings
00294                 //              if (difference > 0)
00295                 //                  then: direction is towards end
00296                 //              if (difference < 0)
00297                 //                  then: direction is towards home (AKA root)
00298                 {
00299                     var currentTarget = inContainer.ActiveNode;
00300                     var nextTarget = setActiveNodeAction.NextActiveNode;
00301 
00302                     while (currentTarget.Parent != nextTarget.Parent)
00303                     {
00304                         if (currentTarget.Parent is null || nextTarget.Parent is null)
00305                             break;
00306 
00307                         currentTarget = currentTarget.Parent;
00308                         nextTarget = nextTarget.Parent;
00309                     }
00310                     
00311                     direction = nextTarget.IndexAmongSiblings - currentTarget.IndexAmongSiblings;
00312                 }
00313 
00314                 if (direction > 0)
00315                 {
00316                     // Move down
00317 
00318                     var moveDownAction = new MoveDownAction(
00319                         setActiveNodeAction.ContainerKey,
00320                         true,
00321                         false);
00322 
00323                     var previousNode = outContainer.ActiveNode;
00324 
00325                     while (true)
00326                     {
00327                         outContainer = PerformMoveDown(
00328                             outContainer,
00329                             moveDownAction);
00330 
00331                         if (previousNode.Key == outContainer.ActiveNode.Key)
00332                         {
00333                             // No change occurred, avoid an infinite loop and break
00334                             break;
00335                         }
00336                         else
00337                         {
00338                             previousNode = outContainer.ActiveNode;
00339                         }
00340 
00341                         if (setActiveNodeAction.NextActiveNode.Key ==
00342                                 outContainer.ActiveNode.Key)
00343                         {
00344                             // Target acquired
00345                             break;
00346                         }
00347                     }
00348                 }
00349                 else if (direction < 0)
00350                 {
00351                     // Move up
00352 
00353                     var moveUpAction = new MoveUpAction(
00354                         setActiveNodeAction.ContainerKey,
00355                         true,
00356                         false);
00357 
00358                     var previousNode = outContainer.ActiveNode;
00359 
00360                     while (true)
00361                     {
00362                         outContainer = PerformMoveUp(
00363                             outContainer,
00364                             moveUpAction);
00365 
00366                         if (previousNode.Key == outContainer.ActiveNode.Key)
00367                         {
00368                             // No change occurred, avoid an infinite loop and break
00369                             break;
00370                         }
00371                         else
00372                         {
00373                             previousNode = outContainer.ActiveNode;
00374                         }
00375 
00376                         if (setActiveNodeAction.NextActiveNode.Key ==
00377                                 outContainer.ActiveNode.Key)
00378                         {
00379                             // Target acquired
00380                             break;
00381                         }
00382                     }
00383                 }
00384                 else
00385                 {
00386                     // The next target is the same as the current target.
00387                     return outContainer;
00388                 }
00389             }
00390             else
00391             {
00392                 if (setActiveNodeAction.NextActiveNode is null)
00393                 {
00394                     selectedNodeListWasCleared = true;
00395     
00396                     outContainer = inContainer with
00397                     {
00398                         SelectedNodeList = ImmutableList<TreeViewNoType>.Empty
00399                     };
00400                 }
00401                 else if (!setActiveNodeAction.AddSelectedNodes)
00402                 {
00403                     selectedNodeListWasCleared = true;
00404     
00405                     outContainer = inContainer with
00406                     {
00407                         SelectedNodeList = new TreeViewNoType[] 
00408                         {
00409                             setActiveNodeAction.NextActiveNode
00410                         }.ToImmutableList()
00411                     };
00412                 }
00413                 else
00414                 {
00415                     var alreadyExistingIndex = inContainer.SelectedNodeList.FindIndex(
00416                         x => setActiveNodeAction.NextActiveNode.Equals(x));
00417                     
00418                     if (alreadyExistingIndex != -1)
00419                     {
00420                         inContainer = inContainer with
00421                         {
00422                             SelectedNodeList = inContainer.SelectedNodeList.RemoveAt(
00423                                 alreadyExistingIndex)
00424                         };
00425                     }
00426 
00427                     outContainer = inContainer with
00428                     {
00429                         SelectedNodeList = inContainer.SelectedNodeList.Insert(
00430                             0,
00431                             setActiveNodeAction.NextActiveNode)
00432                     };
00433                 }
00434             }
00435             
00436             if (selectedNodeListWasCleared)
00437             {
00438                 foreach (var node in inSelectedNodeList)
00439                 {
00440                     PerformMarkForRerender(node);
00441                 }
00442             }
00443 
00444             return outContainer;
00445         }
