00461         private static TreeViewContainer PerformMoveLeft(
00462             TreeViewContainer inContainer,
00463             MoveLeftAction moveLeftAction)
00464         {
00465             var outContainer = inContainer;
00466 
00467             if (moveLeftAction.AddSelectedNodes)
00468                 return outContainer;
00469 
00470             if (outContainer.ActiveNode is null)
00471                 return outContainer;
00472 
00473             if (outContainer.ActiveNode.IsExpanded &&
00474                 outContainer.ActiveNode.IsExpandable)
00475             {
00476                 outContainer.ActiveNode.IsExpanded = false;
00477 
00478                 var reRenderNodeAction = new ReRenderNodeAction(
00479                     outContainer.Key,
00480                     outContainer.ActiveNode);
00481 
00482                 return PerformReRenderNode(outContainer, reRenderNodeAction);
00483             }
00484 
00485             if (outContainer.ActiveNode.Parent is not null)
00486             {
00487                 var setActiveNodeAction = new SetActiveNodeAction(
00488                     outContainer.Key,
00489                     outContainer.ActiveNode.Parent,
00490                     false,
00491                     false);
00492 
00493                 outContainer = PerformSetActiveNode(
00494                     outContainer,
00495                     setActiveNodeAction);
00496             }
00497 
00498             return outContainer;
00499         }
