00501         private static TreeViewContainer PerformMoveDown(
00502             TreeViewContainer inContainer,
00503             MoveDownAction moveDownAction)
00504         {
00505             var outContainer = inContainer;
00506 
00507             if (outContainer.ActiveNode.IsExpanded &&
00508                 outContainer.ActiveNode.ChildList.Any())
00509             {
00510                 var nextActiveNode = outContainer.ActiveNode.ChildList[0];
00511 
00512                 var setActiveNodeAction = new SetActiveNodeAction(
00513                     outContainer.Key,
00514                     nextActiveNode,
00515                     moveDownAction.AddSelectedNodes,
00516                     moveDownAction.SelectNodesBetweenCurrentAndNextActiveNode);
00517 
00518                 outContainer = PerformSetActiveNode(
00519                     outContainer,
00520                     setActiveNodeAction);
00521             }
00522             else
00523             {
00524                 var target = outContainer.ActiveNode;
00525 
00526                 while (target.Parent is not null &&
00527                        target.IndexAmongSiblings == target.Parent.ChildList.Count - 1)
00528                 {
00529                     target = target.Parent;
00530                 }
00531 
00532                 if (target.Parent is null ||
00533                     target.IndexAmongSiblings == target.Parent.ChildList.Count - 1)
00534                 {
00535                     return outContainer;
00536                 }
00537 
00538                 var nextActiveNode = target.Parent.ChildList[
00539                     target.IndexAmongSiblings +
00540                     1];
00541 
00542                 var setActiveNodeAction = new SetActiveNodeAction(
00543                     outContainer.Key,
00544                     nextActiveNode,
00545                     moveDownAction.AddSelectedNodes,
00546                     moveDownAction.SelectNodesBetweenCurrentAndNextActiveNode);
00547 
00548                 outContainer = PerformSetActiveNode(
00549                     outContainer,
00550                     setActiveNodeAction);
00551             }
00552 
00553             return outContainer;
00554         }
