00556         private static TreeViewContainer PerformMoveUp(
00557             TreeViewContainer inContainer,
00558             MoveUpAction moveUpAction)
00559         {
00560             var outContainer = inContainer;
00561 
00562             if (outContainer?.ActiveNode?.Parent is null)
00563                 return outContainer;
00564 
00565             if (outContainer.ActiveNode.IndexAmongSiblings == 0)
00566             {
00567                 var setActiveNodeAction = new SetActiveNodeAction(
00568                     outContainer.Key,
00569                     outContainer.ActiveNode!.Parent,
00570                     moveUpAction.AddSelectedNodes,
00571                     moveUpAction.SelectNodesBetweenCurrentAndNextActiveNode);
00572 
00573                 outContainer = PerformSetActiveNode(
00574                     outContainer,
00575                     setActiveNodeAction);
00576             }
00577             else
00578             {
00579                 var target = outContainer.ActiveNode.Parent.ChildList[
00580                     outContainer.ActiveNode.IndexAmongSiblings - 1];
00581 
00582                 while (true)
00583                 {
00584                     if (target.IsExpanded &&
00585                         target.ChildList.Any())
00586                     {
00587                         target = target.ChildList.Last();
00588                     }
00589                     else
00590                     {
00591                         break;
00592                     }
00593                 }
00594 
00595                 var setActiveNodeAction = new SetActiveNodeAction(
00596                     outContainer.Key,
00597                     target,
00598                     moveUpAction.AddSelectedNodes,
00599                     moveUpAction.SelectNodesBetweenCurrentAndNextActiveNode);
00600 
00601                 outContainer = PerformSetActiveNode(
00602                     outContainer,
00603                     setActiveNodeAction);
00604             }
00605 
00606             return outContainer;
00607         }
