00609         private static TreeViewContainer PerformMoveRight(
00610             TreeViewContainer inContainer,
00611             MoveRightAction moveRightAction)
00612         {
00613             var outContainer = inContainer;
00614 
00615             if (outContainer is null || outContainer.ActiveNode is null)
00616                 return outContainer;
00617 
00618             if (moveRightAction.AddSelectedNodes)
00619                 return outContainer;
00620 
00621             if (outContainer.ActiveNode is null)
00622                 return outContainer;
00623 
00624             if (outContainer.ActiveNode.IsExpanded)
00625             {
00626                 if (outContainer.ActiveNode.ChildList.Any())
00627                 {
00628                     var setActiveNodeAction = new SetActiveNodeAction(
00629                         outContainer.Key,
00630                         outContainer.ActiveNode.ChildList[0],
00631                         moveRightAction.AddSelectedNodes,
00632                         moveRightAction.SelectNodesBetweenCurrentAndNextActiveNode);
00633 
00634                     outContainer = PerformSetActiveNode(
00635                         outContainer,
00636                         setActiveNodeAction);
00637                 }
00638             }
00639             else if (outContainer.ActiveNode.IsExpandable)
00640             {
00641                 outContainer.ActiveNode.IsExpanded = true;
00642 
00643                 moveRightAction.LoadChildListAction.Invoke(
00644                     outContainer.ActiveNode);
00645             }
00646 
00647             return outContainer;
00648         }
