00650         private static TreeViewContainer PerformMoveHome(
00651             TreeViewContainer inContainer,
00652             MoveHomeAction moveHomeAction)
00653         {
00654             var outContainer = inContainer;
00655 
00656             TreeViewNoType target;
00657 
00658             if (outContainer.RootNode is TreeViewAdhoc)
00659             {
00660                 if (outContainer.RootNode.ChildList.Any())
00661                     target = outContainer.RootNode.ChildList[0];
00662                 else
00663                     target = outContainer.RootNode;
00664             }
00665             else
00666             {
00667                 target = outContainer.RootNode;
00668             }
00669 
00670             var setActiveNodeAction = new SetActiveNodeAction(
00671                 outContainer.Key,
00672                 target,
00673                 moveHomeAction.AddSelectedNodes,
00674                 moveHomeAction.SelectNodesBetweenCurrentAndNextActiveNode);
00675 
00676             return PerformSetActiveNode(
00677                 outContainer,
00678                 setActiveNodeAction);
00679         }
00680 
00681         private static TreeViewContainer PerformMoveEnd(
00682             TreeViewContainer inContainer,
00683             MoveEndAction moveEndAction)
00684         {
00685             var outContainer = inContainer;
00686 
00687             var target = outContainer.RootNode;
00688 
00689             while (target.IsExpanded && target.ChildList.Any())
00690             {
00691                 target = target.ChildList.Last();
00692             }
00693 
00694             var setActiveNodeAction = new SetActiveNodeAction(
00695                 outContainer.Key,
00696                 target,
00697                 moveEndAction.AddSelectedNodes,
00698                 moveEndAction.SelectNodesBetweenCurrentAndNextActiveNode);
00699 
00700             return PerformSetActiveNode(
00701                 outContainer,
00702                 setActiveNodeAction);
00703         }
