00066     public static async Task HandleNewCSharpProjectAsync(
00067         string projectTemplateShortName,
00068         string cSharpProjectAbsolutePathString,
00069         IFileSystemProvider fileSystemProvider,
00070         IEnvironmentProvider environmentProvider)
00071     {
00072         if (projectTemplateShortName == BlazorWasmEmptyProjectTemplate.ShortName)
00073             await HandleBlazorWasmEmptyProjectTemplateAsync(cSharpProjectAbsolutePathString, fileSystemProvider, environmentProvider)
00074                 .ConfigureAwait(false);
00075         else if (projectTemplateShortName == BlazorServerSideEmptyProjectTemplate.ShortName)
00076             await HandleBlazorServerSideEmptyProjectTemplateAsync(cSharpProjectAbsolutePathString, fileSystemProvider, environmentProvider)
00077                 .ConfigureAwait(false);
00078         else if (projectTemplateShortName == ClassLibProjectTemplate.ShortName)
00079             await HandleClassLibProjectTemplateAsync(cSharpProjectAbsolutePathString, fileSystemProvider, environmentProvider)
00080                 .ConfigureAwait(false);
00081         else if (projectTemplateShortName == RazorClassLibProjectTemplate.ShortName)
00082             await HandleRazorLibProjectTemplateAsync(cSharpProjectAbsolutePathString, fileSystemProvider, environmentProvider)
00083                 .ConfigureAwait(false);
00084         else if (projectTemplateShortName == ConsoleAppProjectTemplate.ShortName)
00085             await HandleConsoleAppProjectTemplateAsync(cSharpProjectAbsolutePathString, fileSystemProvider, environmentProvider)
00086                 .ConfigureAwait(false);
00087         else if (projectTemplateShortName == XUnitProjectTemplate.ShortName)
00088             await HandleXUnitProjectTemplateAsync(cSharpProjectAbsolutePathString, fileSystemProvider, environmentProvider)
00089                 .ConfigureAwait(false);
00090         else
00091             throw new NotImplementedException($"The {nameof(ProjectTemplate.ShortName)}: '{projectTemplateShortName}' was not recognized.");
00092     }
