00538     public static Guid GetProjectTypeGuid(string projectTemplateShortName)
00539     {
00540         // I'm not going to DRY up the string "FAE04EC0-301F-11D3-BF4B-00C04F79EFBC" for now,
00541         // because I don't fully understand its purpose.
00542 
00543         if (projectTemplateShortName == BlazorWasmEmptyProjectTemplate.ShortName)
00544             return Guid.Parse("FAE04EC0-301F-11D3-BF4B-00C04F79EFBC");
00545         else if (projectTemplateShortName == BlazorServerSideEmptyProjectTemplate.ShortName)
00546             return Guid.Parse("FAE04EC0-301F-11D3-BF4B-00C04F79EFBC");
00547         else if (projectTemplateShortName == ClassLibProjectTemplate.ShortName)
00548             return Guid.Parse("FAE04EC0-301F-11D3-BF4B-00C04F79EFBC");
00549         else if (projectTemplateShortName == RazorClassLibProjectTemplate.ShortName)
00550             return Guid.Parse("FAE04EC0-301F-11D3-BF4B-00C04F79EFBC");
00551         else if (projectTemplateShortName == ConsoleAppProjectTemplate.ShortName)
00552             return Guid.Parse("FAE04EC0-301F-11D3-BF4B-00C04F79EFBC");
00553         else if (projectTemplateShortName == XUnitProjectTemplate.ShortName)
00554             return Guid.Parse("FAE04EC0-301F-11D3-BF4B-00C04F79EFBC");
00555         else
00556             throw new NotImplementedException($"The {nameof(ProjectTemplate.ShortName)}: '{projectTemplateShortName}' was not recognized.");
00557     }
